  { id: 'u2', name: 'Manuel' },
  { id: 'u3', name: 'Julie' },
];






class Users extends Component(){
  constructor(){
    this.state={
      showUsers: true,
      moreState:'T'
    }
  }
  toggleUsersHandler()
  {
    this.setState({showUsers:false})
  }

render()
{
  return( 
    <div className={classes.users}>
      <button onClick={this.toggleUsersHandler}>
        {this.showUsers ? 'Hide' : 'Show'} Users
      </button>
      {this.showUsers && this.usersList}
    </div>
  );
}
}

